#include<iostream>
using namespace std;
const int MAXN = 10000+20;
int fa[MAXN],ra[MAXN];//father和rank,记录大哥是谁和小弟个数 
int N,M;
void init(){//输入 
    cin>>N>>M;
    for(int i=1;i<=N;i++)//初始化 ，每个人都是独立的个体，都是有尊严（？）的人 
        fa[i]=i;
