        fa[yy]=xx;//于是xx就是yy的爸爸了x 
        ++ra[xx];
    }else{//这个次元yy赢了【嗯，yy 
        fa[xx]=yy;
        ++ra[yy];
    }
}
void sol(){
    for(int z=0;z<M;z++){
        int ord,x,y;//命令和x，y 
        cin>>ord>>x>>y;
        if(ord==1) bin(x,y);
        else if(fi(x)==fi(y)) cout<<'Y'<<endl;//如果是同一个组织的就，，就同一个组织嘛 
             else cout<<'N'<<endl;
    }
}
int main(){
    init();
    sol();
    return 0;
}
