```cpp
// treap my 数组

#include <algorithm>
#include <iostream>
#include <cstdio>
#define FOR(i,n,m) for(int i=n;i<=m;++i)
#define il inline
using namespace std;

#define min(a,b) ((a)<(b)?(a):(b))
#define max(a,b) ((a)>(b)?(a):(b))
static char buf[100000],*pa(buf),*pb(buf);
#define gc pa==pb&&(pb=(pa=buf)+fread(buf,1,100000,stdin),pa==pb)?EOF:*pa++	//玄学快读
il int read()
{
    register int x(0);register int f(1);register char c(gc);
    while((c<'0'||c>'9')&&c!='-')c=gc;
    c=='-'?f=-1,c=gc:f;
    while(c>='0'&&c<='9')x=x*10+(c^48),c=gc;
    return f*x;
}

const int N=100100,INF=(1<<29);
struct Treap {
    int lc,rc,key,pri,size,cnt;
}a[N];
#define c(x) a[x].cnt
#define s(x) a[x].size
#define p(x) a[x].pri
#define v(x) a[x].key
#define rc(x) a[x].rc
#define lc(x) a[x].lc
#define up(k) s(k)=s(rc(k))+s(lc(k))+c(k);
//懒人的宏定义方便书写

int tot,rt;

il void zig(int &k) {	//左旋 
    int y=lc(k);
    lc(k)=rc(y);
    rc(y)=k;
    s(y)=s(k);
    up(k)
    k=y;
}

il void zag(int &k) {	//右旋 
    int y=rc(k);
    rc(k)=lc(y);
    lc(y)=k;
    s(y)=s(k);
    up(k)
    k=y;
}

il void insert(int &k,const int &key) {	//	插入 
    if(!k) {
        k=++tot;
        v(k)=key;
        p(k)=rand();
        c(k)=s(k)=1;
        lc(k)=rc(k)=0;
        return; 
    }
    else ++s(k);
    if(v(k)==key) ++c(k);
    else if(key<v(k)) {
        insert(lc(k),key);	//向左搜 
        if(p(lc(k))<p(k)) zig(k);	//左儿子优先级小，左旋 
    }
    else {
        insert(rc(k),key);
        if(p(rc(k))<p(k)) zag(k);
    }
    return;
}

il void del(int &k,const int &key) {	//删除元素 
    if(v(k)==key) {
        if(c(k)>1) --c(k),--s(k);
        else if(!lc(k) || !rc(k)) k=lc(k)+rc(k);
        else if(p(lc(k))<p(rc(k))) zig(k),del(k,key);
        else zag(k),del(k,key);
        return;
    }
    --s(k);
    if(key<v(k)) del(lc(k),key);
    else del(rc(k),key);
    return;
}

il int querypre(int key) {	//前驱 
    int x=rt,res=-INF;
    while(x) {
        if(v(x)<key) res=v(x),x=rc(x);
        else x=lc(x);
    }
    return res;
}

il int querysuf(int key) {	//后继 
    int x=rt,res=INF;
    while(x) {
        if(v(x)>key) res=v(x),x=lc(x);
        else x=rc(x);
    }
    return res;
}

il int querykth(int k) { //查找第k大元素 
    int x=rt;
    while(x) {
        if(s(lc(x))<k&&s(lc(x))+c(x)>=k) return v(x);
        if(s(lc(x))>=k) x=lc(x);
        else k-=s(lc(x))+c(x),x=rc(x);
    }
    return 0;
}

il int querdrank(int key) {	//查找元素排名 
    int x=rt,res=0;
    while(x) {
        if(key==v(x)) return res+s(lc(x))+1;
        if(key<v(x)) x=lc(x);
        else res+=s(lc(x))+c(x),x=rc(x);
    }
    return res;
}

void print(int x)
{
    if(!x) return;
    print(lc(x));
    cout<<x<<" "<<s(x)<<endl;
    print(rc(x));
}

void work() {
    int n=read();

    FOR(i,1,n) {
        int a=read(),x=read();
        if(a==1) insert(rt,x);
        if(a==2) del(rt,x);
        if(a==3) cout<<querdrank(x)<<endl;
        if(a==4) cout<<querykth(x)<<endl;
        if(a==5) cout<<querypre(x)<<endl;
        if(a==6) cout<<querysuf(x)<<endl;
    }
    return;
}

int main() {


    work();

    return 0;
}
