inline void newNode(int &pos,int v){
	pos=++cnt,size[pos]=1,val[pos]=v;
}
inline void copyNode(int x,int y){
	size[x]=size[y],ls[x]=ls[y],rs[x]=rs[y],val[x]=val[y];
