```cpp
#include <bits/stdc++.h>

using namespace std;

int cnt,root,n,i,x,opt,ans,now;
struct node{
    int son[3],fa,num,size,same;
}t[2000010];


void update(int i)
{
    t[i].size=t[t[i].son[0]].size+t[t[i].son[1]].size+t[i].same;
}


int whichson(int i)
{
    if (t[t[i].fa].son[0]==i) return 0;
    else return 1;
}


void rotate(int x)
{
    int fa=t[x].fa,gfa=t[fa].fa;
    int whichx=whichson(x),whichfa=whichson(fa);
    t[t[x].son[!whichx]].fa=fa;
    t[fa].son[whichx]=t[x].son[!whichx];
    t[x].son[!whichx]=fa;
    t[gfa].son[whichfa]=x;
    t[fa].fa=x;
    t[x].fa=gfa;
    update(fa);update(x);
}


void splay(int x,int y)
{
    int fa,gfa;
    if (x==y) return;
    while (t[x].fa!=y)
    {
        fa=t[x].fa;gfa=t[fa].fa;
        if (gfa!=y)
        {
            if (whichson(fa)==whichson(x)) rotate(fa);
            else rotate(x);
        }
        rotate(x);
    }
    if (!y) root=x;
}


void insert(int i,int x,int fa)
{
    if (!i)
    {
        cnt++;
        t[cnt].fa=fa;
        t[cnt].num=x;t[cnt].size=t[cnt].same=1;
        if (x<t[fa].num) t[fa].son[0]=cnt;
        else t[fa].son[1]=cnt;
        if (cnt==1) root=1;
        now=cnt;
        return;
    }
    if (x==t[i].num)
    {
        t[i].size++;t[i].same++;
        now=i;
        return;
    }
    if (x<t[i].num) insert(t[i].son[0],x,i);
    else insert(t[i].son[1],x,i);
    update(i);
}


int findnum(int x)
{
    int i=root,sum=0;
    while (i)
    {
        if (t[t[i].son[0]].size+t[i].same>=x)
        {
            if (t[t[i].son[0]].size<x) return i;
            sum=i;i=t[i].son[0];
        }
        else
        {
            x-=t[t[i].son[0]].size+t[i].same;
            sum=i;i=t[i].son[1];
        }
    }
    return sum;
}


int findrank(int x)
{
    int i=root,sum=0,fa=0;
    while (i)
    {
        if (t[i].num<x)
        {
            sum+=t[t[i].son[0]].size+t[i].same;
            fa=i;i=t[i].son[1];
        }
        else
        {
            fa=i;i=t[i].son[0];
        }
    }
    splay(fa,0);
    return sum;
}


int pred(int x)
{
    int i=root,sum=0;
    while (i)
    {
        if (t[i].num>=x) i=t[i].son[0];
        else
        {
            sum=i;i=t[i].son[1];
        }
    }
    return sum;
}


int succ(int x)
{
    int i=root,sum=0;
    while (i)
    {
        if (t[i].num<=x) i=t[i].son[1];
        else
        {
            sum=i;i=t[i].son[0];
        }
    }
    return sum;
}


void del(int x)
{
    int predd=pred(x),succc=succ(x);
    splay(predd,0);splay(succc,root);
    int rs=t[root].son[1],rsls=t[rs].son[0];
    if (t[rsls].same>1)
    {
        t[rsls].same--;t[rsls].size--;
    }
    else
    {
        t[rs].son[0]=0;
        t[rsls].same--;t[rsls].size--;
    }
    update(rs);update(root);
}


int main()
{
    scanf("%d",&n);
    insert(root,0x7f7f7f7f,0);
    insert(root,-0x7f7f7f7f,0);
    for (int i=1;i<=n;i++)
    {
        scanf("%d%d",&opt,&x);
        if (opt==1)
        {
            insert(root,x,0);
            splay(now,0);
        }
        if (opt==2)
