                    a.insert(a.begin()+hj,a2);
                }
            }
        } else if(a1==2) {
            cin>>a2;
            int w=erfenfind(0,a.size()-1);删除操作
            if(a[w]==a2) {
                a.erase(a.begin()+w);
            }
            int flag=1;
        } else if(a1==3) {
            cin>>a2;
            int w=erfenfind(0,a.size()-1);//朴素的二分查找。
            int code=w;
            while(a[code]==a[w]&&code>=0) {
                code--;
            }
            if(code<w)
                code++;
            cout<<code+1<<endl;
        } else if(a1==4) {
            cin>>a2;
            cout<<a[a2-1]<<endl;//vector中下标会减一。
        } else if(a1==5) {
            cin>>a2;
            int hj=erfen(0,a.size()-1);
            int code=hj;
            while(a[code]>=a2) {
                code--;
            }//防止有相同的。
            cout<<a[code]<<endl;
        } else if(a1==6) {
            cin>>a2;
            int hj=erfen(0,a.size()-1);
            int code=hj;
            while(a[code]<=a2) {
                code++;
            }//防止有相同的。

            cout<<a[code]<<endl;

        }
    }
    return 0;
}
