完整代码：
```cpp
//code
#include<bits/stdc++.h>
#include<ctime>
#define l(x) tree[x].lson
#define r(x) tree[x].rson
#define v(x) tree[x].val
#define p(x) tree[x].pri
#define c(x) tree[x].cnt
#define s(x) tree[x].size
using namespace std;

const int MAXN = 100010;
const int INF = 1 << 30;

struct TREE {
	int lson, rson, val, pri, cnt, size;
} tree[MAXN];
int id = 0, root = 0;
int n, o, opt;

inline int read() {
	int res = 0, uz = 1;
	char ch = getchar();
	while(ch < '0' || ch > '9') {
		if(ch == '-')
			uz = -1;
		ch = getchar();
	}
	while(ch >= '0' && ch <= '9') {
		res = (res << 3) + (res << 1) + (ch ^ '0');
		ch = getchar();
	}
	return res * uz;
}

inline void update(int &x) {
	s(x) = s(l(x)) + s(r(x)) + c(x);
	return;
}

inline void Zig(int &x) {
	int k = l(x);
	l(x) = r(k), r(k) = x;
	s(k) = s(x);
	update(x); // attention!!
	x = k;
	return;
}

inline void Zag(int &x) {
	int k = r(x);
	r(x) = l(k), l(k) = x;
	s(k) = s(x);
	update(x); // attention!!
	x = k;
	return;
}

inline void Insert(int &x, int v) {
	if(!x) {
		x = ++id;
		v(x) = v, c(x) = s(x) = 1;
		l(x) = r(x) = 0;
		p(x) = rand();
		return;
	}
	s(x)++;
	if(v(x) == v)
		c(x)++;
	else {
		if(v < v(x)) {
			Insert(l(x), v);
			if(p(l(x)) < p(x))
				Zig(x);
		} else {
			Insert(r(x), v);
			if(p(r(x)) < p(x))
				Zag(x);
		}
	}
	return;
}

inline void Delete(int &x, int v) {
	if(v(x) == v) {
		if(c(x) > 1)
			c(x)--, s(x)--;
		else
			if(!l(x) || !r(x))
				x = l(x) + r(x);
			else
				if(p(l(x)) < p(r(x)))
					Zig(x), Delete(x, v);
				else
					Zag(x), Delete(x, v);
		return;
	}
	s(x)--; // attention!! 
	if(v < v(x))
		Delete(l(x), v);
	else
		Delete(r(x), v);
	return;
}

inline int QueryKth(int k) {
	int x = root, _k = k;
	while(x) {
		if(_k > s(l(x)) && _k <= s(l(x)) + c(x))
			return v(x);
		if(_k <= s(l(x)))
			x = l(x);
		else
			_k -= s(l(x)) + c(x), x = r(x);
	}
	return 0;
}

inline int QueryPre(int v) {
	int x = root, res = -INF;
	while(x) {
		if(v(x) < v)
			res = v(x), x = r(x);
		else
			x = l(x);
	}
	return res;
}

inline int QuerySuf(int v) {
	int x = root, res = INF;
	while(x) {
		if(v(x) > v)
			res = v(x), x = l(x);
		else
			x = r(x);
	}
	return res;
}

inline int QueryRank(int v) {
	int x = root, rank = 0;
	while(x) {
		if(v(x) == v)
			return rank + s(l(x)) + 1;
		if(v < v(x))
			x = l(x);
		else
			rank += s(l(x)) + c(x), x = r(x);
	}
	return rank;
}

int main() {
