			}
			case 3: {
				printf("%d\n", QueryRank(o));
				break;
			}
			case 4: {
				printf("%d\n", QueryKth(o));
				break;
			}
			case 5: {
				printf("%d\n", QueryPre(o));
				break;
			}
			default: {
				printf("%d\n", QuerySuf(o));
				break;
			}
		}
	}
	return 0;
}
