```cpp
// luogu-judger-enable-o2
#include<bits/stdc++.h>
using namespace std;
const int lb=-10000000;
const int rb=10000000;
struct nod{
    int ls,rs,l,r,value;
}ch[6000000];
int en=0;
int nw(int l,int r){
    en++;
    ch[en].l=l;
    ch[en].r=r;
    return en;
}
void xg(int pos,int value,int o){
    if(ch[o].l==ch[o].r){
        ch[o].value+=value;
        return;
    }
    int &ls=ch[o].ls;
    int &rs=ch[o].rs;
    int mid=ch[o].l+ch[o].r>>1;
    if(pos<=mid){
        if(ls==0){
            ls=nw(ch[o].l,mid);
        }
        xg(pos,value,ls);
    }else {
        if(rs==0){
            rs=nw(mid+1,ch[o].r);
        }
        xg(pos,value,rs);
    }
    ch[o].value=ch[ls].value+ch[rs].value;
    return;
}
int findx(int l,int r,int o){
    if(ch[o].l==l&&ch[o].r==r){
        return ch[o].value;
    }
    int mid=ch[o].l+ch[o].r>>1;
    if(r<=mid){
        if(ch[o].ls==0){
            return 0;
        }
        return findx(l,r,ch[o].ls);
    }else if(l>mid){
        if(ch[o].rs==0){
            return 0;
        } 
        return findx(l,r,ch[o].rs);
    }else{
        if(ch[o].ls==0&&ch[o].rs==0){
            return 0;
        }else if(ch[o].rs==0){
            return  findx(l,mid,ch[o].ls);
        }else if(ch[o].ls==0){
             return findx(mid+1,r,ch[o].rs);	
        }else{
            return findx(l,mid,ch[o].ls)+findx(mid+1,r,ch[o].rs);
        }
    }
} 
int findr(int o,int rank){                                 
    if(ch[o].l==ch[o].r){
        return ch[o].l;
    }
    int ls=ch[o].ls;
    int rs=ch[o].rs;
    if(ch[ls].value>=rank){
    	return findr(ls,rank);
    }else{
    	return findr(rs,rank-ch[ls].value);
    }
}
int main()
{
    int n,opt,x;
    scanf("%d",&n);
    nw(lb,rb); 
    for(int i=1;i<=n;i++){
        scanf("%d%d",&opt,&x);
        if(opt==1){
            xg(x,1,1);
        }else if(opt==2){
            xg(x,-1,1);
        }else if(opt==3){
            int k=findx(lb,x-1,1)+1;
            printf("%d\n",k);
        }else if(opt==4){
            int k=findr(1,x);
            printf("%d\n",k);
        }else if(opt==5){
            int k=findx(lb,x-1,1);
            int g=findr(1,k);
            printf("%d\n",g);
        }else if(opt==6){
            int k1=findx(lb,x,1);
            int k=findx(lb,x+1,1);
            while(k==k1){
                x++;
                k=findx(lb,x+1,1);
            }
            int g=findr(1,k);
            printf("%d\n",g);
        }
    }
} 
