void insert(node *u, node *v) {
    if (u == NULL)return;
    u->size++;
    if (v->x == u->x) {
        u->num++; return;
    }
    else if (v->x > u->x) {
        if (u->r == NULL) {
            u->r = v; v->fa = u;
            while (v->fa != NULL && (v->rnd < v->fa->rnd)) {
                if (v == v->fa->r)lturn(v->fa, v);
                else rturn(v->fa, v);
            }
        }
        else insert(u->r, v);
    }
    else {
