- 每一次插入后，判断一下当前节点所在的子树是否平衡，若不平衡就将这颗子树拍扁重建

```cpp
#include <cstdio>
#include <algorithm>
#define MAXN 100010
#define alpha 0.75
inline int read()
{
    register int x = 0, flag = 1;
    register char ch = getchar();
    while ((ch < '0' || ch > '9') && ch != '-')
        ch = getchar();
    if (ch == '-')
    {
        flag = -1;
        ch = getchar();
    }
    while (ch >= '0' && ch <= '9')
    {
        x = x * 10 + ch - '0';
        ch = getchar();
    }
    return x * flag;
}
struct sgt
{
    int left, right, father, val, size;
    sgt() { this->left = this->right = this->father = -1; }
    sgt(int left, int right, int val, int father = -1, int size = 1) : left(left), right(right), val(val), father(father), size(size){};
} pool[MAXN];
int root = -1, tot = 0;
inline bool balance(int node)
{
    return alpha * pool[node].size >= pool[pool[node].left].size && alpha * pool[node].size >= pool[pool[node].right].size;
}
int temp[MAXN], cnt = 0;
inline int build(int l, int r)
{
    if (l > r)
        return -1;
    int mid = (l + r) >> 1;
    int x = temp[mid];
    pool[x].left = build(l, mid - 1);
    if (~pool[x].left)
        pool[pool[x].left].father = x;
    pool[x].right = build(mid + 1, r);
    if (~pool[x].right)
        pool[pool[x].right].father = x;
    pool[x].size = 1;
    if (~pool[x].left)
        pool[x].size += pool[pool[x].left].size;
    if (~pool[x].right)
        pool[x].size += pool[pool[x].right].size;
    return x;
}
inline void traversal(int x)
{
    if (~x)
    {
        traversal(pool[x].left);
        temp[cnt++] = x;
        traversal(pool[x].right);
    }
}
inline void rebuild(int x)
{
    cnt = 0;
    traversal(x);
    int f = pool[x].father;
    if (~f)
    {
        if (x == pool[f].left)
        {
            pool[f].left = build(0, cnt - 1);
            pool[pool[f].left].father = f;
        }
        else
        {
            pool[f].right = build(0, cnt - 1);
            pool[pool[f].right].father = f;
        }
    }
    else
    {
        root = build(0, cnt - 1);
        pool[root].father = -1;
    }
}
inline void add(int x)
{
    if (root == -1)
    {
        root = 0;
        pool[tot++] = sgt(-1, -1, x);
        return;
    }
    int p = root, f = -1;
    while (~p)
    {
        f = p;
        pool[f].size++;
        if (x <= pool[p].val)
            p = pool[p].left;
        else
            p = pool[p].right;
    }
    pool[tot] = sgt(-1, -1, x, f);
    if (f != -1)
    {
        if (x <= pool[f].val)
            pool[f].left = tot;
        else
            pool[f].right = tot;
    }
    tot++;
    int sg = -1;
    while (~f)
    {
        if (!balance(f))
            sg = f;
        f = pool[f].father;
    }
    if (~sg)
        rebuild(sg);
}
inline int rank(int x)
{
    int p = root, ans = 0;
    while (~p)
    {
        if (x <= pool[p].val)
        {
            p = pool[p].left;
        }
        else
        {
            if (~pool[p].left)
                ans += pool[pool[p].left].size;
            ans++;
            p = pool[p].right;
        }
    }
    return ans;
}
inline int query(int x)
{
    int p = root;
    while (~p)
    {
        if (pool[pool[p].left].size == x - 1)
            return p;
        if (pool[pool[p].left].size > x - 1)
            p = pool[p].left;
        else
        {
            x -= pool[pool[p].left].size;
            x--;
            p = pool[p].right;
        }
    }
    return p;
}
inline int getPos(int x)
{
    int p = root;
    while (~p)
    {
        if (pool[p].val == x)
            return p;
        if (x > pool[p].val)
        {
            p = pool[p].right;
        }
        else
        {
            p = pool[p].left;
        }
    }
    return p;
}
inline void remove(int x)
{
    while ((~pool[x].left) && (~pool[x].right))
    {
        int p = pool[x].left;
        while (~pool[p].right)
            p = pool[p].right;
        pool[x].val = pool[p].val;
        x = p;
    }
    int s = ~pool[x].left ? pool[x].left : pool[x].right;
    if (pool[pool[x].father].left == x)
    {
        pool[pool[x].father].left = s;
        pool[s].father = pool[x].father;
    }
    else
    {
        pool[pool[x].father].right = s;
        pool[s].father = pool[x].father;
    }
    for (int i = pool[x].father; ~i; i = pool[i].father)
        pool[i].size--;
    if (x == root)
        root = s;
}
inline int next(int x)
{
    int p = root;
    int min = 0x7FFFFFFF;
    while (~p)
    {
        if (x < pool[p].val)
        {
            min = std::min(min, pool[p].val);
            p = pool[p].left;
        }
        else
        {
            p = pool[p].right;
        }
    }
    return min;
}
inline int pre(int x)
{
    int p = root;
    int max = 0x80000000;
    while (~p)
    {
        if (x > pool[p].val)
        {
            max = std::max(max, pool[p].val);
            p = pool[p].right;
        }
        else
        {
            p = pool[p].left;
        }
    }
    return max;
}
int n, x;
int main()
{
    tot = 2;
    root = 0;
    pool[0].val = -2147483647;
    pool[0].size = 2;
    pool[0].right = 1;
    pool[0].father = -1;
    pool[1].val = 2147483647;
    pool[1].size = 1;
    pool[1].father = 0;
    n = read();
    while (n--)
    {
        switch (read())
        {
        case 1:
            add(read());
            break;
        case 2:
            remove(getPos(read()));
            break;
        case 3:
            printf("%d\n", rank(read()));
            break;
        case 4:
            printf("%d\n", pool[query(read() + 1)].val);
            break;
        case 5:
            printf("%d\n", pre(read()));
            break;
        case 6:
            printf("%d\n", next(read()));
            break;
        }
    }
    return 0;
}
