总体代码：
```cpp
#include<cstdio>
#include<iostream>
#include<algorithm>
#include<cstring>
#include<climits>
#include<vector>
#include<cstdlib>
#include<ctime>
using namespace std;
#define ls ch[now][0]
#define rs ch[now][1]
inline int read()
{
    int ans = 0,op = 1;
    char ch = getchar();
    while(ch < '0' || ch > '9')
    {
        if(ch == '-') op = -1;
        ch = getchar();
    }
    while(ch >= '0' && ch <= '9')
    {
        (ans *= 10) += ch - '0';
        ch = getchar();
    }
    return ans * op;
}
const int maxn = 1e5 + 5;
int sz[maxn],key[maxn],val[maxn],tot,ch[maxn][2],root;//key:堆相关;val:树相关 
inline void update(int now)
{
    sz[now] = sz[ls] + sz[rs] + 1;
}
void split(int now,int k,int &x,int &y)//x <= k,y > k
{
    if(!now) 
    {        
        x = y = 0;
        return;
    }
    if(val[now] <= k) x = now,split(rs,k,rs,y);
    else y = now,split(ls,k,x,ls);
    update(now);
}
int merge(int x,int y)//val：x <= y 
{
    if(!x || !y) return x + y;
    if(key[x] < key[y])
    {
        ch[x][1] = merge(ch[x][1],y);
        update(x);
        return x;
    }
    else 
    {
        ch[y][0] = merge(x,ch[y][0]);
        update(y);
        return y;
    }
}
int New(int k)
{
    key[++tot] = rand(),val[tot] = k,sz[tot] = 1;
    return tot;
}
void insert(int k)
{
    int x,y;
    split(root,k,x,y);
    root = merge(merge(x,New(k)),y);
}
void del(int k)
{
    int x,y,z;
    split(root,k,x,y);
    split(x,k - 1,x,z);
    z = merge(ch[z][0],ch[z][1]);
    root = merge(x,merge(z,y));
}
void Rank(int k)
{
    int x,y;
    split(root,k - 1,x,y);
    printf("%d\n",sz[x] + 1);
    root = merge(x,y);
}
void fRank(int now,int k)
{
    while(1)
    {
        if(k <= sz[ls]) now = ls;
        else if(k > sz[ls] + 1) k -= sz[ls] + 1,now = rs;
        else { printf("%d\n",val[now]); return;}
    }
}
void pre(int k)
{
    int x,y;
    split(root,k - 1,x,y);
    fRank(x,sz[x]);
    root = merge(x,y);
}
void suc(int k)
{
    int x,y;
    split(root,k,x,y);
    fRank(y,1);
    root = merge(x,y);
}
int main()
{
    int n = read();
    int opt,x;
    while(n--)
    {
        opt = read(),x = read(); 
        if(opt == 1) insert(x);
        if(opt == 2) del(x);
        if(opt == 3) Rank(x); 
        if(opt == 4) fRank(root,x);
        if(opt == 5) pre(x);
        if(opt == 6) suc(x); 
    }
    return 0;
}

```

