inline int get(){
    int n=0;char c;
    while((c=getchar())||1){
        if(c>='0'&&c<='9')break;
        if(c=='-')goto s;
    }n=c-'0';while((c=getchar())||1){
        if(c>='0'&&c<='9')n=n*10+c-'0';
        else return(n);
    }s:while((c=getchar())||1){
        if(c>='0'&&c<='9')n=n*10-c+'0';
        else return(n);
