#include<stdio.h>
int n;
double a[13];
inline double f(const double x){
    double ret=0;
    for(int i=n;i;--i)ret=ret*x+a[i];
    return ret;
}
int main(){
    double left,right,mid;
    scanf("%d%lf%lf",&n,&left,&right);
    for(int i=n;i;--i){
        scanf("%lf",a+i);
        a[i]*=i;
    }
    while(right-left>1e-6)
        if(f(mid=(right+left)/2)>0)left=mid;
        else right=mid;
    printf("%.5lf\n",left);
}
```

