# 枚举

```cpp

#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;

double caculate(double a[],int cnt,double x){
	double sum=0.0,temp=1.0;
	for(int i=0;i<=cnt;i++){
		sum+=temp*a[i];
		temp*=x;
	}
	return sum;
} 
#define ccl(x) caculate(f,n,x)

int n;
double l,r,f[15];

double ans=-20180706.0;

int main(){
	scanf("%d%lf%lf",&n,&l,&r);
	for(int i=0;i<=n;i++) scanf("%lf",f+(n-i));

	double pre=ccl(l),now=ccl(l+0.000001),nxt;
	for(double i=l+0.000001;i<r;i+=0.000001){
		nxt=ccl(i+0.000001);
		if(pre<=now && nxt<=now){
			ans=i;
			break;
		}
		pre=now;
		now=nxt;
	}
	if(ans!=-20180706.0){
		printf("%.5lf\n",ans);
		return 0;
	}
	if(ccl(l)>ccl(r)) printf("%.5lf\n",l);
	else printf("%.5lf\n",r);
	return 0;
}
