#include<bits/stdc++.h>
#define eps 1e-8
#define rep(i,x,y) for(register int i = x;i <= y;++i)
#define dwn(i,x,y) for(register int i = x;i >= y;--i)
#define N 14
using namespace std;
double l,r,a[N];
int n;

double check(double x)
{
	double ans = 0;
	dwn(i,n,0)
		ans=ans*x+a[i];//秦九韶公式
	return ans;
}

int main()
{
	scanf("%d",&n);
	scanf("%lf%lf",&l,&r);
	rep(i,0,n)
		scanf("%lf",&a[n-i]);
	while(r-l>eps)
	{
		double mid1=l+(r-l)/3,mid2=r-(r-l)/3;
		if(check(mid1)>check(mid2))
			r=mid2;
		else 
			l=mid1;
	}
	printf("%.5lf",l);
	return 0;
}
