int n; 
bool pd=1; 
int main()
{
   	scanf("%d",&n);
   	if(n==1)printf("No");//特判。
   	for(int i=2;i*i<=n;i++)//这次枚举到根号n，因为根号n到n之间的数都不是n的因数
   	{
		if(n%i==0)
		{
			pd=0;
			break;
