#include<bits/stdc++.h>
using namespace std;
double a[101][101];
int ABS(double x){
    return x<0?-x:x;
}
int main() {
    int n;
    scanf("%d",&n);
    for(int i=1; i<=n; i++)
        for(int j=1; j<=n+1; j++)
            scanf("%lf",&a[i][j]);
    for(int i=1; i<=n; i++) {
        int bj=i;
        for(int j=i+1; j<=n; j++)
            if(ABS(a[j][i])>ABS(a[bj][i]))
                bj=j;
        for(int j=1; j<=n+1; j++)
            swap(a[bj][j],a[i][j]);//找到系数最大的一个
        if(!a[i][i])
            continue;
        double p=a[i][i];
        for(int j=1;j<=n+1;j++)
            a[i][j]/=p;
        for(int j=1;j<=n;j++)
