    for(int i=n-1; i>=1; i--)
        for(int j=i+1; j<=n; j++)
            a[i][n+1]-=a[j][n+1]*a[i][j];//往回带，求出答案
    for(int i=1; i<=n; i++) {
        if(a[i][n+1]==0)
            printf("x%d=0\n",i);
        else
            printf("x%d=%.2lf\n",i,a[i][n+1]);
    }
    return 0;
}
