        if(fabs(a[i][i])<1e-7){
            puts("No Solution");
            exit(0);
        }
        const double div=a[i][i];
        for(int j=i;j<=n+1;j++)a[i][j]/=div;
        for(int j=i+1;j<=n;j++){
            const double x=a[j][i];
            for(int k=i;k<=n+1;k++)
                a[j][k]-=a[i][k]*x;
        }
