const int N=110;
int n;
double s[N][N],ans[N];
int main()
{
	scanf("%d",&n);
	 for(int i=1;i<=n;i++)
	 {
	 	for(int j=1;j<=n+1;j++)
	 	scanf("%lf",&s[i][j]);

	 }
	 int cnt=0;
	   for(int i=1;i<=n;i++)
      {
	  cnt=0;
      	for(int q=1;q<=n;q++)
      	  if(s[i][q]==0) cnt++;

		   if(cnt==n)
		   {
		   	printf("No Solution");
		   	return 0;
		} 
      	 for(int j=i+1;j<=n;j++)
      	   {
      	   	double tmp=s[j][i]/s[i][i];
			 for(int k=1;k<=n+1;k++)
      	  	{
      	  	  s[j][k]-=s[i][k]*tmp;
				}
			}
	  } 
	  ans[n]=s[n][n+1]/s[n][n];
	   for(int i=n-1;i>=1;i--)
	   {
	       for(int j=n;j>i;j--)
		    s[i][n+1]-=s[i][j]*ans[j];
	         ans[i]=s[i][n+1]/s[i][i];
			}
		for(int i=1;i<=n;i++)
		printf("%.2lf\n",ans[i]);
		return 0;
}
