#include<bits/stdc++.h>
#define ll long long
using namespace std;
const ll mod=1000000007;
ll x,y;

struct P{
	ll m[100][100]; 
};
P a,e,ans;

P jz(P a,P b)
{
	P c;
	for(int i=1;i<=x;i++)
		for(int j=1;j<=x;j++)
			c.m[i][j]=0;
	for(int i=1;i<=x;i++)
      for(int j=1;j<=x;j++)
        for(int k=1;k<=x;k++)
          c.m[i][j]=c.m[i][j]%mod+a.m[i][k]*b.m[k][j]%mod;
    return c; 
}

P mi(P a,ll b)
{
	P ans=e;
    while(b)
    {
        if(b&1) ans=jz(ans,a);
        a=jz(a,a);
        b>>=1;
    }
    return ans;
}

int main()
{
