			cin>>a.m[i][j];
		}
		e.m[i][i]=1;
	}
	ans=mi(a,y);
	for(int i=1;i<=x;i++)
	{
		for(int j=1;j<=x;j++)
		{
			cout<<ans.m[i][j]%mod<<" ";
		}
		cout<<endl;
	}
	return 0;
}
