```
#include <cstdio>
#include <iostream>

using namespace std;
typedef long long ll;
const int ha=1e9+7,MAXN=110;

struct Matrix {
  int n,m;
  ll **a;
  Matrix(int n, int m):n(n),m(m) {
    a=new ll*[n+1];
    for(int i=1;i<=n;i++) {
      a[i]=new ll[m+1];
      for(int j=1;j<=m;j++) a[i][j]=0;
    }
  }
  ~Matrix() {
    for(int i=1;i<=n;i++) delete[] a[i];
    delete[] a;
  }
  Matrix(const Matrix &b):n(b.n),m(b.m) {
    a=new ll*[b.n+1];
    for(int i=1;i<=n;i++) {
      a[i]=new ll[b.m+1];
      std::copy(b.a[i]+1,b.a[i]+1+m,a[i]+1);
    }
  }
  Matrix operator*(Matrix b) {
    Matrix ret(n,b.m);
    for(int i=1;i<=n;i++)
      for(int j=1;j<=b.m;j++)
        for(int k=1;k<=m;k++)
          ret.a[i][j]=(ret.a[i][j]+a[i][k]*b[k][j])%ha;
    return ret;
  }
  Matrix &operator=(Matrix b) {
    for(int i=1;i<=n;i++)
      for(int j=1;j<=m;j++)
        a[i][j]=b.a[i][j];
    return *this;
  }
  ll *operator[](int p) {return a[p];}
  void I() {for(int i=1;i<=n;i++) a[i][i]=1;}
  void print() {
    for(int i=1;i<=n;i++) {
      for(int j=1;j<=n;j++)
        printf("%lld ",a[i][j]);
      puts("");
    }
  }
};

Matrix ksm(Matrix a, ll k) {
  Matrix ret(a.n,a.m);
  ret.I();
  while(k) {
    k&1?ret=ret*a,0:0;
    a=a*a;
    k>>=1;
  }
  return ret;
}

int n;
ll k;

main() {
  cin>>n>>k;
  Matrix A(n,n);
  for(int i=1;i<=n;i++)
    for(int j=1;j<=n;j++)
      scanf("%lld",&A[i][j]);
  ksm(A,k).print();
}
