```cpp
#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
const int M=200;
const int mod=1000000007;
long long int a[M][M];
long long int b[M][M];
long long int n,p;
void mulself(long long int a1[M][M],long long int b1[M][M],int flag)
{
	long long int c[M][M]={0};
	for (int i=1;i<=n;i++)
		for (int j=1;j<=n;j++)
			for (int k=1;k<=n;k++)
				c[i][j]=(c[i][j]%mod+a1[i][k]*b1[k][j]%mod)%mod;
	if (flag) memcpy(a,c,sizeof(c));
	else memcpy(b,c,sizeof(c));
	return ;
}
void poww()
{
	long long int emm=p;
	while (emm)
	{
		if (emm&1) mulself(b,a,0);
		mulself(a,a,1);
		emm>>=1;
	}
	return ;
}
int main()
{
	scanf("%lld%lld",&n,&p);
	for (int i=1;i<=n;i++)
		for (int k=1;k<=n;k++)
			scanf("%lld",&a[i][k]);
	for (int i=1;i<=n;i++) b[i][i]=1;
	poww();
	for (int i=1;i<=n;i++)
	{
		for (int k=1;k<=n;k++)
			cout<<b[i][k]<<" ";
		puts("");
	}
	return 0;
}
