        f[i][1]=min(f[i-1][0],f[i-1][1])+w[i][1];
        f[i][2]=min(f[i-1][1],f[i-1][2])+w[i][2];
    }
    for(int i=1;i<=2;i++) ans=min(ans,f[n-1][i]);
    printf("%d",ans+w[n][2]);
    return 0;
}

