using namespace std;
int n,m,ans,minn=inf;
string s;
struct flag
{
    int w,r,b;
}d[51];
int main()
{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=n;i++)
      {
          cin>>s;
          for(int j=0;j<=m-1;j++)
            {
                if(s[j]=='R') d[i].r++;
                if(s[j]=='B') d[i].b++;
                if(s[j]=='W') d[i].w++;
          }
      }
    ans=(m-d[1].w)+(m-d[n].r);
    int tot=0;
    for(int i=1;i<=n-1;i++)
      {
      for(int j=i+1;j<=n;j++)
        {
            tot=0;
            for(int a=2;a<=i;a++)
              tot+=(m-d[a].w);
            for(int a=i+1;a<=j;a++)
              tot+=(m-d[a].b);
            for(int a=j+1;a<=n-1;a++)
              tot+=(m-d[a].r);
            minn=min(minn,tot);
        }
    }
    cout<<ans+minn;
}
