```
#include <bits/stdc++.h>
#define N 1001
using namespace std;
int t,n;
int rx[2 * N],ry[2 * N];
int dx[] = {1,-1,0,0};
int dy[] = {0,0,1,-1};
bool f[N][N];                  // 地图，用来标记障碍和是否走过
struct que
{
	int x,y,ti;
}q[N * N];                     // 队列，用结构体存储
inline void bfs ()
{
    int h = 1,t = 2;
    q[h] = (que){1,1,0},f[1][1] = 1;     // 初始化
    while (h < t)
    {
        int hx = q[h].x,hy = q[h].y;          
        if (hx == n && hy == n)
        {
            puts("Yes");
            return ;
