                q[t++] = (que){tx,ty,q[h].ti + 1},f[tx][ty] = 1;              // 符合条件入队
        }
        f[rx[q[h].ti + 1]][ry[q[h].ti + 1]] = 1;
        h++;
    }
    puts("No");              // 无法到达
    return ;
}
