#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
using namespace std;
const int Len = 1.5e5 + 5 , SIZE = 405;
int n,m,t,BLOCK[SIZE][SIZE],a[Len];
char s[15];
int main()
{
	scanf("%d %d",&n,&m);
	int t = sqrt(n);
	for(int i = 1 ; i <= n ; i ++) scanf("%d",&a[i]);
	for(int i = 1 ; i <= t ; i ++)
	{
		for(int j = 1 ; j <= n ; j ++)
			BLOCK[i][j % i] += a[j];
	}
	for(int i = 1 ; i <= m ; i ++)
	{
		scanf("%s",s);int x,y;scanf("%d %d",&x,&y);
		if(s[0] == 'A')
		{
			if(x >= t) 
			{
				int res = 0;
				for(int j = y ; j <= n ; j += x) res += a[j];
				printf("%d\n",res); 
			}
			else printf("%d\n",BLOCK[x][y]);
		}
		else
		{
			for(int j = 1 ; j <= t ; j ++)
			{
				BLOCK[j][x % j] -= a[x];
				BLOCK[j][x % j] += y;
			}
			a[x] = y;
		}
	}
	return 0;
}
```

