        int x = read(), y = read();
        if(s[0] == 'A') {
            if(x <= size) write(f[x][y]);
            else {
                int ans = 0;
                for(register int i = y; i <= n; i += x) {
                    ans += a[i];
                }
                write(ans);
            }
        }
        else {
            for(register int i = 1; i <= size; i++) {
                f[i][x % i] += y - a[x];
            }
            a[x] = y;
        }
    }
    return 0;
}
