int n,m,a[1010][1010];
double sx,sy,fx,fy;
int main()
{
	cin>>n>>m;
	for(int i=1;i<=m;i++)
	{
		cin>>sx>>sy>>fx>>fy;
		for(int f=int(ceil(sx));f<=int(floor(fx));f++)
        //ceil()是向上取整，floor()是向下取整，然后再强制转化成整型
		for(int k=int(ceil(sy));k<=int(floor(fy));k++)
		a[f][k]++;
	}
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		cout<<a[i][j]<<" ";
		cout<<endl;
