#include<cstdio>
#include<iostream>
using namespace std;
int n,m,x1,y1,x2,y2;                                        //题目中的(x1,y1)就是按照(i,j)的顺序来的，我一上来被卡了
int ceng[2333][2333]={0};                              //拼音大法好，表示点(i,j)被铺上的地毯层数
void pu(int a,int b,int c,int d)                          //就是从(a,c)铺地毯到(b,d)
{
    for(int i=a;i<=b;i++)
     for(int j=c;j<=d;j++)
     ceng[i][j]++;
    return;
}
int main()
{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=m;i++)
    {
        scanf("%d%d%d%d",&x1,&y1,&x2,&y2); 
        pu(x1,x2,y1,y2);                                     //铺地毯
    }
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=n;j++)
        printf("%d ",ceng[i][j]);                           //输出就行了
        printf("\n");
    }
    return 0;
}
