int n,m,d[1001],c[1001],dp[1001][1001];
int main()
{
    cin >> n >> m;
    for(int i=1;i<=n;i++)
        cin >> d[i];
    for(int i=1;i<=m;i++)
        cin >> c[i];
    memset(dp,0x3f,sizeof(dp));
    memset(dp[0],0,sizeof(dp[0]));
    for(int i=1;i<=n;i++)
        for(int j=1;j<=m;j++)
            dp[i][j]=min(dp[i][j-1],dp[i-1][j-1]+d[i]*c[j]);
    cout << dp[n][m] << endl;
    return 0;
}
```

