			else f[i][j]=min(f[i-1][j-1]+fence[j]*dis[i],f[i][j-1]);
		}
	}
	int ans=1e9;
	for(int i=n;i<=m;i++)ans=min(ans,f[n][i]);
	cout<<ans;
