#### //这道题的其他思路：有兴趣的话可以自行写一下差分前缀和，可能比树状数组还要好写，这里不赘述。


```cpp
#include<cstdio>
#include<iostream>
#include<algorithm>
#define lowbit(x) x&-x
#define lint long long
using namespace std;
lint n,m,ans,tree[100010],A[100010],B[100010],C[100010];//ai*x<-->ci+bi*x
void add(lint pos,lint val){
	for(;pos<=n;pos+=lowbit(pos)){
		tree[pos]+=val;
	}
}
inline lint getsum(lint pos){
	lint ans=0;
	for(;pos;pos-=lowbit(pos)){
		ans+=tree[pos];
	}
	return ans;
} 
inline lint min(int x,int y){return x<y?x:y;}
int main(){
	scanf("%lld%lld",&n,&m);
	lint tmp=0,last=0;
	scanf("%lld",&last);
	for(lint i=2;i<=m;i++){
		scanf("%lld",&tmp);
		if(tmp>last){
			add(last,1);
			add(tmp,-1);
		}else{
			add(tmp,1);
			add(last,-1);
		}//表示经过次数 
		last=tmp;
	}//tree里面1-n-1存储着边的经过次数 
	//for(int i=1;i<=n-1;i++)printf("%lld ",getsum(i));
	for(lint i=1;i<=n-1;i++){
		scanf("%lld%lld%lld",&A[i],&B[i],&C[i]);
		lint sum=getsum(i);
		ans+=min(A[i]*sum,C[i]+B[i]*sum);
	}
	printf("%lld",ans);
	return 0;
} 
```

