void Archmage(unsigned long long x) {
    unsigned long long son = 0;
    for(register unsigned long long i = head[x]; i; i = edge[i].nxt) {
        son++;
        Archmage(edge[i].to);
    }
    if(son == 0) {
        f[x] = a[x];
