		int p1=get(x1,y11,x2,mid),p2=get(x1,mid+1,x2,y2);
		if(p1<=2*m&&p1>=m)out(x1,y11,x2,mid);
		if(p2<=2*m&&p2>=m)out(x1,mid+1,x2,y2);
		if(p1>2*m)y2=mid;
		else y11=mid+1;
	}
	return 0;
}

