{
	cin>>T;		
	while(T--)
	{
		cin>>n;
		sum=0;
		for(int i=1;i<=n;i++)
		{
			cin>>a[i];
			if((n-i+1)&1)sum^=(a[i]-a[i-1]);
		}
		if(sum==0)
		{
			puts("NIE");
		} 
		else
		{
			puts("TAK");
		}
	}
} 
```

