			inline Segment get_seg(int x)const{//解不等式用
				if(k>0) return (Segment){-b/k,(x-b)/k};
				if(k<0) return (Segment){(x-b)/k,-b/k};	
			}
			node operator +(const node &x)const{//重载加号减号
				return (node){k+x.k,b+x.b};
			}
