		inline int check(node x,node y){//判断无解，无数解，返回有解时的系数
			if(x.k==y.k){
				if(x.b==y.b) return inf;
				else return -inf;
			}
			if((y.b-x.b)%(x.k-y.k)) return -inf;
			return (y.b-x.b)/(x.k-y.k);
