			for(int i=head[u];i;i=edge[i].next){
				int v=edge[i].v,w=edge[i].w;
				if(!vis[v]){//没有访问过直接表示
					vis[v]=1;
					p[v]=(node){0,w}-p[u];
					q.push(v); vec.push_back(v);
				}else{//已经访问过联立求解并回代
					int tmp=check((node){0,w}-p[u],p[v]);
					if(tmp!=inf){
						if(res!=inf && res!=tmp) return -inf;
						res=tmp;
					}
