```cpp
#include<bits/stdc++.h>
using namespace std;
int head[500005],pnt[2000005],nxt[2000005],E=0;//前向星建图，边权为1，就不用记录了 
bool vis[500005];//能否被攻击到 
int n,m,k,cnt=0;
int ans[500005];//记录答案 
void add_edge(int a,int b)
{
	pnt[E]=b;
	nxt[E]=head[a];
	head[a]=E++;
}
void init()
{
	memset(head,-1,sizeof(head));
	scanf("%d%d%d",&n,&m,&k);
	for(int i=1;i<=m;++i)
	{
		int x,y;
		scanf("%d%d",&x,&y);
		add_edge(x,y);
		add_edge(y,x);
	}
}
void solve()
{
	for(int i=1;i<=n;++i)
	{
		if(!vis[i])//不在攻击范围内，就建一个新箭塔 
		{
			vis[i]=1;
			ans[++cnt]=i;
			for(int j=head[i];j!=-1;j=nxt[j])
			{
				int v=pnt[j];
				vis[v]=1;
				for(int k=head[v];k!=-1;k=nxt[k])
				{
					int vv=pnt[k];
					vis[vv]=1;
				}
			}//标记在攻击范围内的点 
		}
	}
	printf("%d\n",cnt);
	for(int i=1;i<=cnt;++i)
	{
		printf("%d ",ans[i]);
	}
}
int main()
{
	init();
	solve();
	return 0;
}
```

