#include<bits/stdc++.h>
#define il inline
using namespace std;
typedef long long ll;
il int read(){
	int ans=0,flag=1;
	char ch=getchar();
	while(!isdigit(ch)){if(ch=='-')flag=-1;ch=getchar();}
	while(isdigit(ch))ans=(ans<<3)+(ans<<1)+(ch^48),ch=getchar();
	return ans*flag;
}
const int N=5e5+7,M=1e6+7;
struct edge{
	int to,nxt;
}e[M<<1];
int head[N];
int cnt;
