void dfs(int now,int stp){
	//printf("(%d,%d)\n",now,stp);
	vis[now]=1;
	if(stp==2)return;
	for(int i=head[now];i;i=e[i].nxt){
		dfs(e[i].to,stp+1);
	}
}

int main(){
	n=read(),m=read(),k=read();
	for(int i=1;i<=m;i++){
		int u=read(),v=read();
		add_edge(u,v);
		add_edge(v,u);
	}
	//for(int i=1;i<=2*m;i++){
	//	printf("%d %d\n",e[i].to,e[i].nxt);
	//}
	for(int i=1;i<=n;i++){
		if(!vis[i]){
			ans[++tot]=i;
			dfs(i,0);
		}
	}
	printf("%d\n",tot);
	for(int i=1;i<=tot;i++){
		printf("%d ",ans[i]);
	}
	return 0;
}
