			f[i][0]=f[i-1][0]+2;
			f[i][1]=f[i-1][0]+1;
			f[i][2]=min(f[i-1][0],min(f[i-1][1],f[i-1][2]));
		}
		if(a[i]==0) {
			f[i][0]=f[i-1][0]+1;
			f[i][1]=min(f[i-1][0],f[i-1][1]);
			f[i][2]=f[i-1][2]+1;
		}
		if(a[i]==-1) {
			f[i][0]=f[i-1][0];
			f[i][2]=f[i-1][2]+2;
		}
	}

	ans=inf;

	for(int i=0;i<3;i++) {
		ans=min(ans,f[n][i]);
	}

	if(ans>=inf) printf("BRAK");
	else printf("%lld",ans);

	return 0;
}
```

