code
```cpp
#include <cstdio>
#include <algorithm>
using namespace std;
int main()
{
    int T , a , b , c , d , i , last , ans;
    scanf("%d" , &T);
    while(T -- )
    {
        scanf("%d%d%d%d" , &a , &b , &c , &d);
        for(i = 1 ; i <= b && i <= d ; i = last + 1)
        {
            last = min(b / (b / i) , d / (d / i));
            if(b / last > (a - 1) / last && d / last > (c - 1) / last) ans = last;
        }
        printf("%d\n" , ans);
    }
    return 0;
}
```

