		for (int i = 0 ; i < a ; ++ i) scanf("%s",t[i]);
		for (int i = 0 ; i < n ; ++ i)
			for (int j = 0 ; j < m ; ++ j) tots += c[i][j] = s[i][j] == 'x';
		int cnt = 0;
		for (int i = 0 ; i < a ; ++ i)
			for (int j = 0 ; j < b ; ++ j) if (t[i][j] == 'x') tott ++,p[++ cnt] = node(i - 1,j - 1);
		bool flag = 1;
		if (tots && !tott || tots % tott) {
			puts("NIE");
			continue;
		}
		for (int i = 0 ; i < n ; ++ i) {
			for (int j = 0 ; j < m ; ++ j) {
				if (c[i][j]) {
					int upl = i - p[1].x;
					int upr = j - p[1].y;
					for (int k = 1 ; k <= cnt ; ++ k) {
						int xx = upl + p[k].x,yy = upr + p[k].y;
						if (xx < 0 || xx >= n || yy < 0 || yy >= m) {
							flag = 0;
							break;
						}
						c[xx][yy] ^= 1;
						if (c[xx][yy]) {
							flag = 0;
							break;
						}
					}
				}
				if (!flag) break;
			}
			if (!flag) break;
		}
		puts(flag ? "TAK" : "NIE");
	}
	return 0;
}
