ll l,r,ans,cnt,is[Max+10],prim[Max+10],A[Max+10],B[Max+10];
inline void prep(){
	for(int i=2,j;i<=Max;++i){
		if(!is[i]) prim[++cnt]=i;
		for(j=1;j<=cnt && i*prim[j]<=Max;++j){
