        for(int j=1;j<=seccnt;j++){
            int x=read();x=((x-s)%n+n)%n;
            sec|=(1<<(x));
        }
        for(int j=1;j<=hapcnt;j++){
            int x=read();x=((x-s)%n+n)%n;
            hap|=(1<<(x));
        }
        for(int j=0;j<32;j++)
            if((j&hap)||((sec&j)!=sec)) {
                cal[s][j]++;
            }
    }
    for(int i=0;i<32;i++){
        memset(f,-127/3,sizeof(f));
        f[0][i]=0;
        for(int j=1;j<=n;j++){
            for(int z=0;z<32;z++) f[j][z]=max(f[j-1][(z&15)<<1],f[j-1][((z&15)<<1)|1])+cal[j][z];
        }
        maxn=max(maxn,f[n][i]);
    }
    cout<<maxn;
}
